mapper.update:
UPDATE news_info
title = #{title}, news_image = #{newsImage}, content = #{content}, summary = #{summary}, author = #{author}, status = #{status}, reject_reason = #{rejectReason}, deleted = #{deleted}, is_top = #{isTop}, view_count = #{viewCount}, update_time = NOW()
WHERE id = #{id}

UPDATE news_info
title = #{title}, news_image = #{newsImage}, content = #{content}, summary = #{summary}, author = #{author}, status = #{status}, reject_reason = #{rejectReason}, deleted = #{deleted}, is_top = #{isTop}, view_count = #{viewCount},
WHERE id = #{id}

mapper.select:
SELECT * FROM news_info
WHERE deleted = 0

    AND create_time >= #{startTime}


    AND create_time <= #{endTime}


    AND title LIKE CONCAT('%', #{title}, '%')


    AND summary LIKE CONCAT('%', #{summary}, '%')


    AND news_image LIKE CONCAT('%', #{newsImage}, '%')


    AND author LIKE CONCAT('%', #{author}, '%')


    AND status = #{status}


    AND create_user_id = #{createUserId}

ORDER BY is_top DESC,create_time DESC
LIMIT #{pageSize} OFFSET #{offset}

SELECT COUNT(*) FROM news_info
WHERE deleted = 0

    AND create_time >= #{startTime}


    AND create_time <= #{endTime}


    AND title LIKE CONCAT('%', #{title}, '%')


    AND summary LIKE CONCAT('%', #{summary}, '%')


    AND news_image LIKE CONCAT('%', #{newsImage}, '%')


    AND author LIKE CONCAT('%', #{author}, '%')


    AND status = #{status}


    AND create_user_id = #{createUserId}

mapper.update:
UPDATE news_info
SET status = #{status}, reject_reason = #{reason}
WHERE id = #{id}

UPDATE news_info
SET view_count = #{viewCount}
WHERE id = #{id}
